import React from 'react'
import {mount} from 'enzyme'
import AccountNumber from './'

describe('AccountNumber', () => {
    const defaultAcct = '1234567890'

    describe('render an account number in the proper format', () => {
        const wrapper = mount(
            <AccountNumber displayData={defaultAcct} />
        )

        it('should be a span', () => {
            expect(wrapper.containsMatchingElement(<span>{defaultAcct}</span>)).toBe(true)
        })

        it('should have the class .account', () => {
            expect(wrapper.hasClass('account')).toBe(true)
        })

        it('should have the data-abc-id of accountNumber', () => {
            expect(wrapper.find('.account').prop('data-abc-id')).toBe('accountNumber')
        })
    })

    describe('render a null when nothing is passed in', () => {
        const wrapper = mount(
            <AccountNumber displayData="" />
        )
        
        it('should be null', () => {
            expect(wrapper.html()).toBe(null)
        })
    })

    describe('should allow extra properties', () => {
        const wrapper = mount(
            <AccountNumber displayData={defaultAcct} role="list" />
        )
        
        it('should have the prop role: list', () => {
            expect(wrapper.prop('role')).toBe('list')
        })
    })
})