import React from 'react'
import PropTypes from 'prop-types'
import styles from './accountNumber.styl'

const AccountNumber = (props) => {
    if (!props.displayData) {
        return null
    }

    return <span className={styles.account} data-abc-id="accountNumber">{props.displayData.toUpperCase()}</span>
}

AccountNumber.propTypes = {
    displayData: PropTypes.string,
}

export default AccountNumber