import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from '@abc/protonpack'
import {Address} from '../../export'

export default class AddressExample extends Component {
    render() {
        const address = {
            address1: '123 Fake St.',
            city: 'Springfield',
            state: 'Missouri',
            zipCode: '12345',
        }

        return (
            <div className={this.props.className}>
                <h2>Address</h2>
                <Row>
                    <Col xs={12}>Formats an address to the proper format:</Col>
                    <Col>
                        Input:
                        <code>{JSON.stringify(address)}</code>
                    </Col>
                    <Col>
                        Output: <Address displayData={address} />
                    </Col>
                </Row>
            </div>
        )
    }
}

AddressExample.propTypes = {
    className: PropTypes.string,
}