import React from 'react'
import PropTypes from 'prop-types'
import states from './states'
import ZipCode from 'components/zipCode'

const Address = (props) => {
    const {displayData, ...remain} = props

    if (!displayData || Object.keys(displayData).length === 0) {
        return <div>{'\u00a0'}</div>
    }

    const stateAbbreviation = (displayData.state && displayData.state.length > 2) ? states[displayData.state.toLowerCase()] : displayData.state

    return (
        <div data-abc-id="addressContainer" {...remain}>
            <div data-abc-id="address1">{displayData.address1}</div>
            {displayData.address2 && <div data-abc-id="address2">{displayData.address2}</div>}
            <div><span data-abc-id="city">{displayData.city}</span>, <span data-abc-id="state">{stateAbbreviation}</span> <ZipCode displayData={displayData.zipCode} /></div>
        </div>
    )
}
 
Address.propTypes = {
    displayData: PropTypes.object,
}

export default Address