import React from 'react'
import {mount} from 'enzyme'
import {Input} from '@abc/protonpack'
import EmailInput from './'

describe('Email Input', () => {
    describe('render an email input', () => {
        const wrapper = mount(
            <EmailInput />
        )

        const wrapperElement = wrapper.find(Input)

        it('should contain a <Input />', () => {
            expect(wrapperElement.length).toBe(1)
        })

        it('should have the type of "email"', () => {
            expect(wrapperElement.prop('type')).toBe('email')
        })

        it('should have the data-abc-id of emailButton', () => {
            expect(wrapperElement.prop('data-abc-id')).toBe('emailInput')
        })
    })
})