import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {FormGroup, Label, Input} from '@abc/protonpack'

export default class EmailInput extends Component {
    render() {
        const {placeholder, label, nolabel, statusMessage, ...remain} = this.props

        return (
            <FormGroup statusMessage={statusMessage}>
                {!nolabel && <Label>{label}</Label>}
                <Input type="email" placeholder={placeholder} data-abc-id="emailInput" {...remain} />
            </FormGroup>
        )
    }
}

EmailInput.propTypes = {
    label: PropTypes.string,
    placeholder: PropTypes.string,
    nolabel: PropTypes.bool,
    statusMessage: PropTypes.object,
}

EmailInput.defaultProps = {
    label: 'Email',
    placeholder: 'you@example.com',
    nolabel: false,
}