import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {GoalBar} from '../../export'

export default class GoalBarExample extends Component {
    render() {
        return (
            <div className={this.props.className}>
                <h2>Goal Bar</h2>
                <p>Below Goal</p>
                <GoalBar max={20} goal={16} value={8} />
                <p>Reached Goal</p>
                <GoalBar max={20} goal={16} value={16} />
                <p>Over Goal</p>
                <GoalBar max={20} goal={16} value={18} />
            </div>
        )
    }
}

GoalBarExample.propTypes = {
    className: PropTypes.string,
}
