import React from 'react'
import IconGroup from './'
import Icon from '@abc/quarkicons'
import {mount} from 'enzyme'

describe('IconGroup', () => {
    describe('render a basic icon group', () => {
        const wrapper = mount(
            <IconGroup type="pencil">
                <span>Edit Something</span>
            </IconGroup>
        )

        const wrapperElement = wrapper.find('.container')

        it('should render the label', () => {
            const label = wrapperElement.childAt(0)
            expect(label.type()).toBe('span')
        })

        it('should render an icon', () => {
            const icon = wrapperElement.childAt(1)
            expect(icon.type()).toBe(Icon)
        })
    })
})