import React from 'react'
import PropTypes from 'prop-types'
import Icon from '@abc/quarkicons'
import {SecondaryFont, Button} from '@abc/protonpack'
import styles from './iconGroupLink.styl'

const IconGroupLink = (props) => {
    const {children, type, size, ...rest} = props
    const content = typeof children === 'object' ? children : <span>{children}</span>

    return (
        <SecondaryFont size={size}>
            <Button btnStyle="link" className={styles.container} {...rest}>
                <Icon className={styles.icon} type={type} />
                {content}
            </Button>
        </SecondaryFont>
    )
}

IconGroupLink.propTypes = {
    children: PropTypes.node,
    type: PropTypes.string.isRequired,
    size: PropTypes.string.isRequired,
    onClick: PropTypes.func,
    disabled: PropTypes.bool,
}

IconGroupLink.defaultProps = {
    size: 'sm',
}

export default IconGroupLink
