import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from '@abc/protonpack'
import DefaultIconToggle from './default'
import MultipleIconToggle from './multipleIcon'
import AsyncIconToggle from './async'
import AsyncCancelableIconToggle from './asyncCancelable'

export default class IconToggleExample extends Component {
    render() {
        return (
            <div className={this.props.className}>
                <h2>Icon Toggles</h2>
                <h3>Immediate Toggles</h3>
                <Row>
                    <Col>
                        <h4>Single Icon</h4>
                        <DefaultIconToggle/>
                    </Col>
                    <Col>
                        <h4>Checked/Unchecked Icons</h4>
                        <MultipleIconToggle/>
                    </Col>
                </Row>
                <h3>Async Toggles</h3>
                <Row>
                    <Col>
                        <h4>Non-Cancelable Toggle</h4>
                        <AsyncIconToggle/>
                    </Col>
                    <Col>
                        <h4>Cancelable Toggle</h4>
                        <AsyncCancelableIconToggle/>
                    </Col>
                </Row>
            </div>
        )
    }
}

IconToggleExample.propTypes = {
    className: PropTypes.string,
}
