import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from '@abc/protonpack'
import {InlineEdit} from '../../export'

export default class InlineEditExample extends Component {
    constructor(props) {
        super(props)

        this.state = {
            inputValue: 'Test',
        }
    }

    render() {
        return (
            <div className={this.props.className}>
                <h2>Inline Edit</h2>
                <Row>
                    <Col md={3}>
                        <InlineEdit onEdit={inputValue => this.setState({inputValue})} iconSize="1.25rem" inputProps={{style: {fontSize: '1rem'}}}>
                            <h3 style={{padding: 0}}>{this.state.inputValue}</h3>
                        </InlineEdit>
                    </Col>
                </Row>
            </div>
        )
    }
}

InlineEditExample.propTypes = {
    className: PropTypes.string,
}