import React, {Component} from 'react'
import {List} from '../../../export'
import range from 'lodash/range'
import uniqueId from 'lodash/uniqueId'

const makeNewListItem = (count) => {
    return {
        myId: uniqueId('list_uid_'),
        number: count + 1,
    }
}

export default class DefaultListExample extends Component {
    constructor(props) {
        super(props)

        this.state = {
            count: 6,
            options: range(6).map(makeNewListItem),
        }

        this.renderGenericItem = this.renderGenericItem.bind(this)
    }

    renderGenericItem(test) {
        return <div className="example">{test.number}</div>
    }

    render() {
        return (
            <List
                idKey="myId"
                displayData={this.state.options}
                item={this.renderGenericItem}
                {...this.props}
            />
        )
    }
}
