import React from 'react'
import PropTypes from 'prop-types'
import Icon from '@abc/quarkicons'
import {Col, BaseText, Divider, SecondaryFont} from '@abc/protonpack'
import styles from './noResults.styl'
import classnames from 'classnames'

const ListItemNoResults = ({messages, className, ...remain}) => (
    <Col
        sm={{col: 10, justifyContent: 'center'}}
        md={{col: 6, justifyContent: 'center'}}
        data-abc-id="noresults"
        className={classnames(className, 'noResults')}
        {...remain}
    >
        <div className={styles.iconWrapper}>
            <Icon className={styles.icon} type="search" />
        </div>
        <SecondaryFont size="xl">
            <h2>{messages.noResults}</h2>
        </SecondaryFont>
        <Divider md={12} lg={9} xl={7} />
        <BaseText weight="bold">
            <div>
                <p>{messages.matchSearch}</p>
                <p>{messages.tryAgain}</p>
            </div>
        </BaseText>
    </Col>
)

ListItemNoResults.propTypes = {
    className: PropTypes.string,
    messages: PropTypes.shape({
        noResults: PropTypes.string,
        matchSearch: PropTypes.string,
        tryAgain: PropTypes.string,
    }),
}

export default ListItemNoResults
