import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Pager} from '../../export'

export default class PagerExample extends Component {
    constructor(props) {
        super(props)

        this.state = { 
            locations: 500, 
            page: 1,
        }

        this.onLocationPageChange = this.onLocationPageChange.bind(this)
    }

    onLocationPageChange(pageNumber) {
        this.setState({page: pageNumber})
    }

    render() {
        return (
            <div className={this.props.className}>
                <h2>Pager</h2>
                <Pager
                    changePage={this.onLocationPageChange}
                    totalCount={this.state.locations} pageSize={10}
                    currentPage={this.state.page}
                />
            </div>
        )
    }
}

PagerExample.propTypes = {
    className: PropTypes.string,
}