import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Phone} from '../../export'

export default class PhoneExample extends Component {
    render() {
        return (
            <div className={this.props.className}>
                <h2>Phone Display</h2>
                <div>
                    Displays a correctly formatted phone number: <Phone displayData={{number: '5015551234'}} />
                </div>
                <div>
                    Also Supports extensions: <Phone displayData={{number: '5015551234', extension: '777'}} />
                </div>
            </div>
        )
    }
}

PhoneExample.propTypes = {
    className: PropTypes.string,
}