import React from 'react'
import PropTypes from 'prop-types'
import styles from './phone.styl'

const Phone = (props) => {
    const {displayData, ...remain} = props

    let phoneNumber = <em>none provided</em>
    let extension = ''

    if (displayData) {
        if (displayData.number) {
            phoneNumber = displayData.number.replace(/(\d{3})(\d{3})(\d{4})/, '($1) $2-$3')
        }
        if (displayData.extension) {
            extension = `Ext ${displayData.extension}`
        }
    }

    return (
        <div {...remain} data-abc-id="phone">
            <div className={styles.inlineBlock} data-abc-id="phoneNumber">
                <span>{phoneNumber}</span>{extension && <div className={styles.inlineBlock} data-abc-id="phoneExtension">{'\u00A0'}{extension}</div>}
            </div>
        </div>
    )
}

Phone.propTypes = {
    displayData: PropTypes.shape({
        number: PropTypes.string,
        extension: PropTypes.string,
    }),
    className: PropTypes.string,
}

export default Phone
