import React from 'react'
import PropTypes from 'prop-types'
import styles from './popoverSelectOption.styl'
import classnames from 'classnames'

const PopoverSelectOption = ({children, selected, disabled, grouped, ...option}) => {
    const optionStyle = {
        [styles.popoverSelectOption]: true,
        [styles.grouped]: grouped,
        [styles.disabled]: disabled,
        [styles.selected]: selected,
    }

    return (
        <div tabIndex={0} className={classnames(optionStyle)} {...option}>
            {children}
        </div>
    )
}

PopoverSelectOption.propTypes = {
    children: PropTypes.node.isRequired,
    disabled: PropTypes.bool,
    grouped: PropTypes.bool,
    onClick: PropTypes.func.isRequired,
    selected: PropTypes.bool,
}

export default PopoverSelectOption
