import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from '@abc/protonpack'
import {SubmitButton} from '../../export'

export default class SubmitButtonExample extends Component {
    render() {
        return (
            <div className={this.props.className}>
                <h2>SubmitButton</h2>
                <Row>
                    <Col>
                        <h5>Normal State</h5>
                        <SubmitButton btnStyle="primary">Submit</SubmitButton>
                    </Col>
                    <Col>
                        <h5>In Submitting State</h5>
                        <SubmitButton btnStyle="primary" submitting>Submit</SubmitButton>
                    </Col>
                </Row>
            </div>
        )
    }
}

SubmitButtonExample.propTypes = {
    className: PropTypes.string,
}