import React from 'react'
import PropTypes from 'prop-types'
import Loading from 'components/loading'
import {Button} from '@abc/protonpack'
import styles from './submitButton.styl'

const SubmitButton = (props) => {
    const {children, submitting, disabled, ...remain} = props
    const content = typeof children === 'object' ? children : <span>{children}</span>

    return (
        <Button
            type="submit"
            {...remain}
            disabled={disabled || submitting}
        >
            {submitting ? <Loading timeout={0} className={styles.loading} /> : <span>{content}</span>}
        </Button>
    )
}

SubmitButton.propTypes = {
    children: PropTypes.node.isRequired,
    submitting: PropTypes.bool,
    disabled: PropTypes.bool,
}

export default SubmitButton
