import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {ZipCode} from '../../export'

export default class ZipCodeExample extends Component {
    render() {
        return (
            <div className={this.props.className}>
                <h2>ZipCode Display</h2>
                <div>
                    Displays a correctly formatted zipcode: <ZipCode displayData="72015" />
                </div>
                <div>
                    Also Supports extensions: <ZipCode displayData="720151234" />
                </div>
            </div>
        )
    }
}

ZipCodeExample.propTypes = {
    className: PropTypes.string,
}