import React from 'react'
import PropTypes from 'prop-types'

const ZipCode = (props) => {
    const {displayData, ...remain} = props
    const zip = (displayData && displayData.length > 5) ? displayData.replace(/(\d{5})(\d)/, '$1-$2') : displayData

    return <span data-abc-id="zipCode" {...remain}>{zip}</span>
}

ZipCode.propTypes = {
    displayData: PropTypes.string,
}

export default ZipCode