import quarkicons from '@abc/quarkicons/dist/quarkicons' // eslint-disable-line no-unused-vars
import protonpack from '@abc/protonpack/dist/style.css' // eslint-disable-line no-unused-vars
import React, {Component} from 'react'
import ReactDOM from 'react-dom'
import {Row, Col, Header} from '@abc/protonpack'

import KitchenSink from './examples/KitchenSink'

import {locations} from '../mock_data/locations'
import persons from '../mock_data/persons'
import styles from './index.styl'

class FedAgents extends Component {
    constructor() {
        super()

        this.state = {
            users: {persons: persons.slice(0, 12), page: 1},
            places: {locations: locations.slice(0,5), page: 1},
            hoverCardState: false,
        }

        this.onLocationPageChange = this.onLocationPageChange.bind(this)
        this.onPersonPageChange = this.onPersonPageChange.bind(this)
        this.onHoverCardToggle = this.onHoverCardToggle.bind(this)
    }

    onLocationPageChange(pageNumber) {
        const start = (pageNumber - 1) * 5
        const end = pageNumber * 5

        this.setState({places: {locations: locations.slice(start, end), page: pageNumber}})
    }

    onPersonPageChange(pageNumber) {
        const start = (pageNumber - 1) * 12
        const end = pageNumber * 12

        this.setState({users: {persons: persons.slice(start, end), page: pageNumber}})
    }

    onHoverCardToggle() {
        this.setState({hoverCardState: !this.state.hoverCardState})
    }

    renderGenericItem(test) {
        return <div className="example">{test.number}</div>
    }

    render() {
        return (
            <div className={styles.page}>
                <Header fixed className={styles.header}>
                    <Row sm={{justifyContent: 'between'}} style={{height: '100%'}}>
                        <Col xs sm={{col: 'auto', alignSelf: 'center', justifyContent: 'end'}}>
                            <div>I'm the header kid</div>
                        </Col>
                        <Col xs sm={{col: 'auto', alignSelf: 'center'}}>
                            Get some help son
                        </Col>
                    </Row>
                </Header>
                <KitchenSink />
            </div>
        )
    }
}

ReactDOM.render(<FedAgents/>, document.getElementById('app'))
