module.exports = {
    url: 'http://www.abcfinancial.com/test',
    dummyData: '{"fry" : "phillip"}',
    mockJsonResponse: (url, data, response, status = 200, statusText) => {

        if(typeof data !== 'undefined' && data.body !== null && typeof data.body !== 'undefined') {
            response = data.body;
        } 

        return new window.Response(response, {
            url,
            status,
            statusText,
            headers: {
                'Content-type': 'application/json'
            }
        })
    },
    mockTextResponse: (url, data, response, status = 200, statusText = 'OK') => {

        return new window.Response(response, {
            url,
            status,
            statusText,
            headers: {
                'Content-type': 'text/plain'
            }
        })
    }
};