import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup, ReactSelect, Label} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import {statuses} from '../data'
import './alertBrander.scss'

class AlertBrander extends Component {
    constructor(props) {
        super(props)

        this.state = {
            status: '',
        }
    }

    render() {
        const {styles} = this.props
        const {status} = this.state

        return (
            <Row xs={{justifyContent: 'center'}}>
                <Col sm={12}>
                    <FormGroup size="sm" >
                        <LabelGroup label="Border Radius" standaloneLabel >
                            <BranderRange
                                id="alertBorderRadius"
                                CSSProperty="--alertBorderRadius"
                                styles={styles}
                                min={0}
                                max={30}
                            />
                        </LabelGroup>
                    </FormGroup>
                </Col>
                <Col sm={6} style={{marginRight: 'auto'}}>
                    <FormGroup size="sm">
                        <Label>Status</Label>
                        <ReactSelect
                            size="sm"
                            options={statuses}
                            onChange={status => this.setState({status})}
                            value={this.state.status}
                            placeholder="Select One..."
                        />
                    </FormGroup>
                </Col>
                {status &&
                    <Col sm={12}>
                        <Row>
                            <Col sm={12}>
                                <ColorPicker
                                    customProperty={`--alert${status}`}
                                    label="Background"
                                    className={`alert-brander-${status.toLowerCase()}`}
                                    color={String(styles.getPropertyValue(`--alert${status}`)).trim()}
                                    isCSSProperty
                                />
                            </Col>
                            <Col sm={6}>
                                <ColorPicker
                                    customProperty={`--alert${status}Text`}
                                    label="Text"
                                    className={`alert-brander-${status.toLowerCase()}-text`}
                                    color={String(styles.getPropertyValue(`--alert${status}Text`)).trim()}
                                />
                            </Col>
                            <Col sm={6}>
                                <ColorPicker
                                    customProperty={`--alert${status}Border`}
                                    label="Border"
                                    className={`alert-brander-${status.toLowerCase()}-border`}
                                    color={String(styles.getPropertyValue(`--alert${status}Border`)).trim()}
                                />
                            </Col>
                        </Row>
                    </Col>
                }
            </Row>
        )
    }
}

AlertBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default AlertBrander