import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import './bodyBrander.scss'

const BodyBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--bodyBackground"
                    label="Background"
                    className="body-brander-background"
                    color={String(styles.getPropertyValue('--bodyBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--bodyText"
                    label="Text"
                    className="body-brander-text"
                    color={String(styles.getPropertyValue('--bodyText')).trim()}
                />
            </Col>
            <Col sm={6}>
                <FormGroup size="sm" style={{marginBottom: 0}}>
                    <LabelGroup label="Border Radius" standaloneLabel>
                        <BranderRange
                            id="borderRadius"
                            CSSProperty="--borderRadius"
                            styles={styles}
                            min={0}
                            max={30}
                        />
                    </LabelGroup>
                </FormGroup>
            </Col>
        </Row>
    )
}

BodyBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default BodyBrander