import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import './cardBrander.scss'

const CardBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <FormGroup size="sm" >
                    <LabelGroup label="Border Radius" standaloneLabel >
                        <BranderRange
                            id="cardBorderRadius"
                            CSSProperty="--cardBorderRadius"
                            styles={styles}
                            min={0}
                            max={30}
                        />
                    </LabelGroup>
                </FormGroup>
            </Col>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--cardBackground"
                    label="Background"
                    className="card-brander-background"
                    color={String(styles.getPropertyValue('--cardBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--cardLightGrayBackground"
                    label="Light Background"
                    className="card-brander-light-gray-background"
                    color={String(styles.getPropertyValue('--cardLightGrayBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--cardBorder"
                    label="Border"
                    className="card-brander-border"
                    color={String(styles.getPropertyValue('--cardBorder')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--cardShadow"
                    label="Shadow"
                    className="card-brander-shadow"
                    showAlpha
                    color={String(styles.getPropertyValue('--cardShadow')).trim()}
                />
            </Col>
        </Row>
    )
}

CardBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default CardBrander