import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import './checkboxBrander.scss'

const CheckboxBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--checkboxBackground"
                    label="Background"
                    className="checkbox-brander-background"
                    color={String(styles.getPropertyValue('--checkboxBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--checkboxCheck"
                    label="Check"
                    className="checkbox-brander-check"
                    color={String(styles.getPropertyValue('--checkboxCheck')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--checkboxBorder"
                    label="Border"
                    className="checkbox-brander-border"
                    color={String(styles.getPropertyValue('--checkboxBorder')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--checkboxShadow"
                    label="Shadow"
                    className="checkbox-brander-shadow"
                    showAlpha
                    color={String(styles.getPropertyValue('--checkboxShadow')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--checkboxDisabledCheck"
                    label="Disabled Check"
                    className="checkbox-brander-disabled-check"
                    color={String(styles.getPropertyValue('--checkboxDisabledCheck')).trim()}
                />
            </Col>
        </Row>
    )
}

CheckboxBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default CheckboxBrander