import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import './dividerBrander.scss'

const DividerBrander = props => (
    <Row xs={{justifyContent: 'center'}}>
        <Col sm={12}>
            <ColorPicker
                customProperty="--dividerBorder"
                label="Border"
                className="divider-brander-border"
                color={String(props.styles.getPropertyValue('--dividerBorder')).trim()}
            />
        </Col>
    </Row>
)

DividerBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default DividerBrander