import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import './inlineHoverBrander.scss'

const InlineHoverBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <FormGroup size="sm" >
                    <LabelGroup label="Hover Border Radius" standaloneLabel >
                        <BranderRange
                            id="inlineHoverHoverBorderRadius"
                            CSSProperty="--inlineHoverHoverBorderRadius"
                            styles={styles}
                            min={0}
                            max={30}
                        />
                    </LabelGroup>
                </FormGroup>
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--inlineHoverIcon"
                    label="Icon"
                    className="inline-hover-brander-icon"
                    color={String(styles.getPropertyValue('--inlineHoverIcon')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--inlineHoverHoverIcon"
                    label="Hover Icon"
                    className="inline-hover-brander-hover-icon"
                    color={String(styles.getPropertyValue('--inlineHoverHoverIcon')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--inlineHoverHoverIconBackground"
                    label="Hover Icon Background"
                    className="inline-hover-brander-hover-icon-background"
                    color={String(styles.getPropertyValue('--inlineHoverHoverIconBackground')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--inlineHoverHoverBackground"
                    label="Hover Background"
                    className="inline-hover-brander-hover-background"
                    color={String(styles.getPropertyValue('--inlineHoverHoverBackground')).trim()}
                />
            </Col>
            <Col sm={{col: 6, pull: 3}}>
                <ColorPicker
                    customProperty="--inlineHoverHoverBorder"
                    label="Hover Border"
                    className="inline-hover-brander-hover-border"
                    showAlpha
                    color={String(styles.getPropertyValue('--inlineHoverHoverBorder')).trim()}
                />
            </Col>
        </Row>
    )
}

InlineHoverBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default InlineHoverBrander