import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import './linkBrander.scss'

const LinkBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--linkText"
                    label="Text"
                    className="link-brander-text"
                    color={String(styles.getPropertyValue('--linkText')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--linkHoverText"
                    label="Hover Text"
                    className="link-brander-hover-text"
                    color={String(styles.getPropertyValue('--linkHoverText')).trim()}
                />
            </Col>
        </Row>
    )
}

LinkBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default LinkBrander