import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import './modalBrander.scss'

const ModalBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <FormGroup size="sm" >
                    <LabelGroup label="Border Radius" standaloneLabel >
                        <BranderRange
                            id="modalBorderRadius"
                            CSSProperty="--modalBorderRadius"
                            styles={styles}
                            min={0}
                            max={30}
                        />
                    </LabelGroup>
                </FormGroup>
            </Col>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--modalBackground"
                    label="Background"
                    className="modal-brander-background"
                    color={String(styles.getPropertyValue('--modalBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--modalText"
                    label="Text"
                    className="modal-brander-text"
                    color={String(styles.getPropertyValue('--modalText')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--modalBorder"
                    label="Border"
                    className="modal-brander-border"
                    color={String(styles.getPropertyValue('--modalBorder')).trim()}
                />
            </Col>
            <Col sm={{col: 6, pull: 3}}>
                <ColorPicker
                    customProperty="--modalShadow"
                    label="Shadow"
                    className="modal-brander-shadow"
                    showAlpha
                    color={String(styles.getPropertyValue('--modalShadow')).trim()}
                />
            </Col>
        </Row>
    )
}

ModalBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default ModalBrander