import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import './popoverBrander.scss'

const PopoverBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <FormGroup size="sm" >
                    <LabelGroup label="Border Radius" standaloneLabel >
                        <BranderRange
                            id="popoverBorderRadius"
                            CSSProperty="--popoverBorderRadius"
                            styles={styles}
                            min={0}
                            max={30}
                        />
                    </LabelGroup>
                </FormGroup>
            </Col>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--popoverBackground"
                    label="Background"
                    className="popover-brander-background"
                    color={String(styles.getPropertyValue('--popoverBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--popoverText"
                    label="Text"
                    className="popover-brander-text"
                    color={String(styles.getPropertyValue('--popoverText')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--popoverBorder"
                    label="Border"
                    className="popover-brander-border"
                    color={String(styles.getPropertyValue('--popoverBorder')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--popoverShadow"
                    label="Shadow"
                    className="popover-brander-shadow"
                    showAlpha
                    color={String(styles.getPropertyValue('--popoverShadow')).trim()}
                />
            </Col>
            <Col sm={6}>
                <ColorPicker
                    customProperty="--popoverDivider"
                    label="Divider"
                    className="popover-brander-divider"
                    showAlpha
                    color={String(styles.getPropertyValue('--popoverDivider')).trim()}
                />
            </Col>
        </Row>
    )
}

PopoverBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default PopoverBrander