import React from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import './tooltipBrander.scss'

const TooltipBrander = (props) => {
    const {styles} = props

    return (
        <Row xs={{justifyContent: 'center'}}>
            <Col sm={12}>
                <FormGroup size="sm" >
                    <LabelGroup label="Border Radius" standaloneLabel >
                        <BranderRange
                            id="tooltipBorderRadius"
                            CSSProperty="--tooltipBorderRadius"
                            styles={styles}
                            min={0}
                            max={30}
                        />
                    </LabelGroup>
                </FormGroup>
            </Col>
            <Col sm={12}>
                <ColorPicker
                    customProperty="--tooltipBackground"
                    label="Background"
                    className="tooltip-brander-background"
                    color={String(styles.getPropertyValue('--tooltipBackground')).trim()}
                    isCSSProperty
                />
            </Col>
            <Col sm={{col: 6, pull: 3}}>
                <ColorPicker
                    customProperty="--tooltipText"
                    label="Text"
                    className="tooltip-brander-text"
                    color={String(styles.getPropertyValue('--tooltipText')).trim()}
                />
            </Col>
        </Row>
    )
}

TooltipBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default TooltipBrander