import React, {Component} from 'react'
import PropTypes from 'prop-types'
import {Row, Col} from 'layout/export'
import ColorPicker from '../colorPicker'
import {FormGroup, LabelGroup, ReactSelect, Label} from 'formElements/export'
import BranderRange from '../common/range/BranderRange'
import {statuses} from '../data'
import './ufoBrander.scss'

class UfoBrander extends Component {
    constructor(props) {
        super(props)

        this.state = {
            status: '',
        }
    }

    render() {
        const {styles} = this.props
        const {status} = this.state

        return (
            <Row xs={{justifyContent: 'center'}}>
                <Col sm={12}>
                    <FormGroup size="sm" >
                        <LabelGroup label="Border Radius" standaloneLabel >
                            <BranderRange
                                id="ufoBorderRadius"
                                CSSProperty="--ufoBorderRadius"
                                styles={styles}
                                min={0}
                                max={30}
                            />
                        </LabelGroup>
                    </FormGroup>
                </Col>
                <Col sm={6} style={{marginRight: 'auto'}}>
                    <FormGroup size="sm">
                        <Label>Status</Label>
                        <ReactSelect
                            size="sm"
                            options={statuses}
                            onChange={status => this.setState({status})}
                            value={this.state.status}
                            placeholder="Select One..."
                        />
                    </FormGroup>
                </Col>
                {status &&
                    <Col sm={6}>
                        <ColorPicker
                            customProperty={`--ufo${status}`}
                            label="Border & Feedback"
                            className={`ufo-brander-${status.toLowerCase()}`}
                            color={String(styles.getPropertyValue(`--ufo${status}`)).trim()}
                        />
                    </Col>
                }
                <Col sm={12}>
                    <ColorPicker
                        customProperty="--ufoBackground"
                        label="Background"
                        className="ufo-brander-background"
                        color={String(styles.getPropertyValue('--ufoBackground')).trim()}
                        isCSSProperty
                    />
                </Col>
                <Col sm={6}>
                    <ColorPicker
                        customProperty="--ufoText"
                        label="Text"
                        className="ufo-brander-text"
                        color={String(styles.getPropertyValue('--ufoText')).trim()}
                    />
                </Col>
                <Col sm={6}>
                    <ColorPicker
                        customProperty="--ufoDivider"
                        label="Divider"
                        className="ufo-brander-divider"
                        color={String(styles.getPropertyValue('--ufoDivider')).trim()}
                    />
                </Col>
                <Col sm={{col: 6, pull: 3}}>
                    <ColorPicker
                        customProperty="--ufoShadow"
                        label="Shadow"
                        className="ufo-brander-shadow"
                        color={String(styles.getPropertyValue('--ufoShadow')).trim()}
                        showAlpha
                    />
                </Col>
            </Row>
        )
    }
}

UfoBrander.propTypes = {
    styles: PropTypes.object.isRequired,
}

export default UfoBrander