import React from 'react'
import {shallow} from 'enzyme'
import NewComponentTemplate from './'

describe('NewComponentTemplate', () => {
    describe('example test for a new component', () => {
        const wrapper = shallow(
            <NewComponentTemplate someProp="xyz" someOtherProp="junk" />
        ) 

        it('should be a div', () => {
            expect(wrapper.type()).toBe('div')
        })

        it('should have the class .row__example--flex', () => {
            expect(wrapper.hasClass('row__example--flex')).toBe(true)
        })

        it('should have the default data-abc-id', () => {
            expect(wrapper.prop('data-abc-id')).toBe('defaultAbcId')
        })
    })
})