import React from 'react'
import {shallow} from 'enzyme'
import ButtonGroup from './'

describe('ButtonGroup', () => {
    describe('render a button group', () => {
        const buttonGroup = shallow(
            <ButtonGroup>xyz</ButtonGroup>
        ) 

        const bgDiv = buttonGroup.find('div')

        it('should be a div', () => {
            expect(bgDiv.matchesElement(<div>xyz</div>)).toBe(true)
        })

        it('should have the class .btn-group', () => {
            expect(bgDiv.hasClass('btn-group')).toBe(true)
        })

        it('should have the data-abc-id of buttonGroup', () => {
            expect(bgDiv.prop('data-abc-id')).toBe('buttonGroup')
        })
    })

    describe('render a vertical button group', () => {
        const buttonGroup = shallow(
            <ButtonGroup vertical>xyz</ButtonGroup>
        )

        const bgDiv = buttonGroup.find('div')

        it('should be a div', () => {
            expect(bgDiv.matchesElement(<div>xyz</div>)).toBe(true)
        }) 

        it('should have the class .btn-group-vertical', () => {
            expect(bgDiv.hasClass('btn-group-vertical')).toBe(true)
        })

        it('should have the data-abc-id of buttonGroup', () => {
            expect(bgDiv.prop('data-abc-id')).toBe('buttonGroup')
        })
    })

    describe('render proper button group size attributes', () => {
        const buttonGroup = shallow(
            <ButtonGroup size="lg">xyz</ButtonGroup>
        ) 

        const bgDiv = buttonGroup.find('div')

        it('should be a div', () => {
            expect(bgDiv.matchesElement(<div>xyz</div>)).toBe(true)
        }) 

        it('should have the class .btn-group-lg', () => {
            expect(bgDiv.hasClass('btn-group-lg')).toBe(true)
        })

        it('should have the data-abc-id of buttonGroup', () => {
            expect(bgDiv.prop('data-abc-id')).toBe('buttonGroup')
        })
    })

    describe('render btn group with additional classes', () => {
        const buttonGroup = shallow(
            <ButtonGroup className="someclass anotherone">xyz</ButtonGroup>
        )

        const bgDiv = buttonGroup.find('div')

        it('should be a div', () => {
            expect(bgDiv.matchesElement(<div>xyz</div>)).toBe(true)
        })

        it('should have the class .btn-group', () => {
            expect(bgDiv.hasClass('btn-group')).toBe(true)
        })

        it('should have the data-abc-id of buttonGroup', () => {
            expect(bgDiv.prop('data-abc-id')).toBe('buttonGroup')
        })

        it('should have the classes .someclass and .anotherone', () => {
            expect(bgDiv.hasClass('someclass')).toBe(true)
            expect(bgDiv.hasClass('anotherone')).toBe(true)
        })
    })
})