import React from 'react'
import {mount} from 'enzyme'
import CardGroup from './'

describe('Card', () => {
    describe('render a cardGroup with the correct classes', () => {
        const wrapper = mount(
            <CardGroup>xyz</CardGroup>
        )

        it('should be a div', () => {
            expect(wrapper.containsMatchingElement(<div>xyz</div>)).toBe(true)
        })

        it('should have the class .card', () => {
            expect(wrapper.hasClass('cardGroup')).toBe(true)
        })

        it('should have the data-abc-id of cardGroup', () => {
            expect(wrapper.find('.cardGroup').prop('data-abc-id')).toBe('cardGroup')
        })
    })

    describe('should allow data-abc-id to be overridden', () => {
        const wrapper = mount(
            <CardGroup data-abc-id="swamp">xyz</CardGroup>
        )
        
        it('should have the prop data-abc-id: swamp', () => {
            expect(wrapper.find('.cardGroup').prop('data-abc-id')).toBe('swamp')
        })
    })


    describe('should allow extra properties', () => {
        const wrapper = mount(
            <CardGroup role="swamp">xyz</CardGroup>
        )
        
        it('should have the prop role: swamp', () => {
            expect(wrapper.prop('role')).toBe('swamp')
        })
    })
})