import React from 'react'
import PropTypes from 'prop-types'
import Controls from './'

const INLINE_STYLES = [
    {label: 'B', iconType: 'bold-solid', style: 'BOLD'},
    {label: 'I', iconType: 'italic-solid', style: 'ITALIC'},
    {label: 'U', iconType: 'underline-solid', style: 'UNDERLINE'},
]

const InlineStyleControls = (props) => {
    const currentStyle = props.editorState.getCurrentInlineStyle()

    return (
        <span className="RichEditor__controls">
            {INLINE_STYLES.map(type =>
                <Controls
                    key={type.label}
                    label={type.label}
                    iconType={type.iconType}
                    onToggle={() => props.onToggle(type.style)}
                    active={currentStyle.has(type.style)}
                />
            )}
        </span>
    )
}

InlineStyleControls.propTypes = {
    editorState: PropTypes.object,
    onToggle: PropTypes.func,
}

export default InlineStyleControls
