import React from 'react'
import {shallow} from 'enzyme'
import Checkbox from './'

describe('render a checkbox input', () => {
    const checkBox = shallow(
        <Checkbox id="checkbox"/>
    )

    const inputElm = checkBox.find('input')

    it('should be an input', () => {
        expect(inputElm.matchesElement(<input type="checkbox"/>)).toBe(true)
    })

    it('should have the class .form-check-input', () => {
        expect(inputElm.hasClass('form-check-input')).toBe(true)
    })

    it('should have the data-abc-id checkboxInput', () => {
        expect(inputElm.prop('data-abc-id')).toBe('checkboxInput')
    })

    it('should have the className checkboxContainer', () => {
        expect(checkBox.is('.checkboxContainer')).toBe(true)
    })
})

describe('checkbox input with class', () => {
    const checkBox = shallow(
        <Checkbox className="foo"/>
    )

    it('checkbox should have the className foo', () => {
        expect(checkBox.is('.foo')).toBe(true)
    })
    it('should have the className checkboxContainer', () => {
        expect(checkBox.is('.checkboxContainer')).toBe(true)
    })
})

describe('checkbox input with custom size ', () => {
    const mediumCheckBox = shallow(
        <Checkbox id="checkbox" size="md"/>
    )

    const largeCheckBox = shallow(
        <Checkbox id="checkbox" size="lg"/>
    )

    it('checkbox should have the size md', () => {
        expect(mediumCheckBox.is('.checkbox-size-md')).toBe(true)
    })
    it('should have the className checkboxContainer', () => {
        expect(mediumCheckBox.is('.checkboxContainer')).toBe(true)
    })

    it('checkbox should have the size lg', () => {
        expect(largeCheckBox.is('.checkbox-size-lg')).toBe(true)
    })
    it('should have the className checkboxContainer', () => {
        expect(largeCheckBox.is('.checkboxContainer')).toBe(true)
    })
})
