import React from 'react'
import {shallow} from 'enzyme'
import FauxInput from './index'

describe('<FauxInput />', () => {
    describe('Test basic render', () => {
        const fauxInput = shallow(
            <FauxInput />
        )

        it('Should have the base class', () => {
            expect(fauxInput.hasClass('fauxInput')).toBe(true)
        })
        it('Should not have the focused class', () => {
            expect(fauxInput.hasClass('fauxInput--focus')).toBe(false)
        })
    })

    describe('Test custom class', () => {
        const fauxInput = shallow(
            <FauxInput className="test" />
        )

        it('Should have the base class and the custom class', () => {
            expect(fauxInput.hasClass('fauxInput')).toBe(true)
            expect(fauxInput.hasClass('test')).toBe(true)
        })
    })

    describe('Test focused flag', () => {
        const fauxInput = shallow(
            <FauxInput focused />
        )

        it('Should have the class focused class and the base class', () => {
            expect(fauxInput.hasClass('fauxInput')).toBe(true)
            expect(fauxInput.hasClass('fauxInput--focus')).toBe(true)
        })
    })
})