import React, {Component} from 'react'
import PropTypes from 'prop-types'
import classnames from 'classnames'

export default class FauxInput extends Component {
    render() {
        // eslint-disable-next-line no-unused-vars
        const {focused, invalid, noPadding, className, children, ...remain} = this.props
        const classes = classnames(
            'fauxInput',
            {'fauxInput--focus': focused},
            {'fauxInput--noPadding': noPadding},
            {'form-control-danger': invalid},
            className,
        )

        return (
            <div className={classes} {...remain}>
                {children}
            </div>
        )
    }
}

FauxInput.propTypes = {
    children: PropTypes.node.isRequired,
    className: PropTypes.string,
    focused: PropTypes.bool,
    invalid: PropTypes.bool,
    noPadding: PropTypes.bool,
}