import React from 'react'
import {shallow} from 'enzyme'
import Form from './'

describe('Form', () => {
    describe('render a basic form', () => {
        const form = shallow(
            <Form>xyz</Form>
        ) 

        const formElm = form.find('form')

        it('should be a div', () => {
            expect(formElm.matchesElement(<form>xyz</form>)).toBe(true)
        })

        it('should have the class .form-container', () => {
            expect(formElm.hasClass('form-container')).toBe(true)
        })
    })

    describe('render an inline form', () => {
        const form = shallow(
            <Form inline>xyz</Form>
        ) 

        const formElm = form.find('form')

        it('should be a div', () => {
            expect(formElm.matchesElement(<form>xyz</form>)).toBe(true)
        })

        it('should have the class .form-container', () => {
            expect(formElm.hasClass('form-container')).toBe(true)
        })

        it('should have the class .form-inline', () => {
            expect(formElm.hasClass('form-inline')).toBe(true)
        })
    })

    describe('render a basic form with extra classes', () => {
        const form = shallow(
            <Form className="123 abc">xyz</Form>
        ) 

        const formElm = form.find('form')

        it('should be a div', () => {
            expect(formElm.matchesElement(<form>xyz</form>)).toBe(true)
        })

        it('should have the class .form-container', () => {
            expect(formElm.hasClass('form-container')).toBe(true)
        })

        it('should have the class .abc and .123', () => {
            expect(formElm.hasClass('abc')).toBe(true)
            expect(formElm.hasClass('123')).toBe(true)
        })
    })

    describe('render a form with inline and extra classes', () => {
        const form = shallow(
            <Form inline className="xyz 123">xyz</Form>
        ) 

        const formElm = form.find('form')

        it('should be a div', () => {
            expect(formElm.matchesElement(<form>xyz</form>)).toBe(true)
        })

        it('should have the class .form-container', () => {
            expect(formElm.hasClass('form-container')).toBe(true)
        })

        it('should have the class .form-inline', () => {
            expect(formElm.hasClass('form-inline')).toBe(true)
        })

        it('should have the class .abc and .123', () => {
            expect(formElm.hasClass('xyz')).toBe(true)
            expect(formElm.hasClass('123')).toBe(true)
        })
    })
})