import React, {Component} from 'react'
import PropTypes from 'prop-types'

export default class Form extends Component {
    buildClasses(className, inline) {
        const classes = ['form-container']
        if (inline) {
            classes.push('form-inline')
        }
        if (className) {
            classes.push(className)
        }

        return classes.join(' ')
    }

    render() {
        const {className, inline, ...remain} = this.props
        const classNames = this.buildClasses(className, inline)

        return (
            <form className={classNames} {...remain} noValidate>
                {this.props.children}
            </form>
        )
    }
}

Form.propTypes = {
    children: PropTypes.node.isRequired,
    className: PropTypes.string,
    inline: PropTypes.bool,
}