import PropTypes from 'prop-types'
import React, {Component} from 'react'
import {Row} from 'layout/export'

export default class FormSection extends Component {
    buildClasses() {
        let classes = this.props.size ? `form-section-${this.props.size}` : 'form-section'

        if (this.props.className) {
            classes += ` ${this.props.className}`
        }

        return classes
    }

    render() {
        const {className, children, ...remain} = this.props

        const classes = this.buildClasses(className)

        return (
            <Row className={classes} data-abc-id="formSection" {...remain}>
                {children}
            </Row>
        )
    }
}

FormSection.propTypes = {
    className: PropTypes.string,
    children: PropTypes.node,
    size: PropTypes.string,
}