import React from 'react'
import {mount} from 'enzyme'
import Radio from './'

describe('render a radio input', () => {
    const radio = mount(
        <Radio name="radiogroup" id="bender" label="Bender" />
    )

    const inputElement = radio.find('input')

    it('should be an input', () => {
        expect(inputElement.matchesElement(<input type="radio" />)).toBe(true)
    })

    it('should not have the class .form-control', () => {
        expect(inputElement.hasClass('form-control')).toBe(false)
    })

    it('should have the class .form-check-input', () => {
        expect(inputElement.hasClass('form-check-input')).toBe(true)
    })

    it('should have the data-abc-id radioInput', () => {
        expect(inputElement.prop('data-abc-id')).toBe('radioInput')
    })
})