import React from 'react'
import PropTypes from 'prop-types'
import Container from '../layout/container'

export const Header = (props) => {
    const {fluid, fixed, containerId, ...remain} = props
    const classes = fixed ? 'header header--isFixed' : 'header'
    const headerId = {id: containerId}

    return (
        <header {...remain} data-abc-id="header">
            <div className={classes} {...headerId}>
                <Container fluid={fluid}>
                    {props.children}
                </Container>
            </div>
            {fixed && <div className="displace" />}
        </header>
    )
}

Header.propTypes = {
    fluid: PropTypes.bool,
    fixed: PropTypes.bool,
    children: PropTypes.node,
    className: PropTypes.string,
    containerId: PropTypes.string,
}

export default Header