import React from 'react'
import {shallow} from 'enzyme'
import EventImage from './'
import EventImageSVG from './eventImageSVG'

describe('EventImage', () => {
    describe('default component', () => {
        const wrapper = shallow(
            <EventImage src="" />
        )

        it('should have a data-abc-id prop', () => {
            expect(wrapper.prop('data-abc-id')).toBe('eventImage')
        })

        it('should have a default size prop', () => {
            expect(wrapper.prop('size')).toBe('4.375rem')
        })

        it('should use the EventImageSVG for placeholder', () => {
            expect(wrapper.prop('placeholder').type).toBe(EventImageSVG)
        })
    })

    describe('will handle props', () => {
        const wrapper = shallow(
            <EventImage className="fake-className" src="fake-img-src" size="111px" />
        )

        it('should assign the src attribute of the img', () => {
            expect(wrapper.prop('src')).toBe('fake-img-src')
        })

        it('should have the passed className', () => {
            expect(wrapper.hasClass('fake-className')).toBe(true)
        })
    })
})
