import React from 'react'
import PropTypes from 'prop-types'
import BalancedImage from '../balancedImage'
import EventImageSVG from './eventImageSVG'

const EventImage = (props) => {
    const {src, size, ...remain} = props
    const imageSize = size || '4.375rem'

    return (
        <BalancedImage
            src={src}
            placeholder={(<EventImageSVG/>)}
            size={imageSize}
            round
            data-abc-id="eventImage"
            {...remain}
        />
    )
}

EventImage.propTypes = {
    src: PropTypes.string,
    size: PropTypes.string,
}

EventImage.defaultProps = {
    src: '',
}

export default EventImage
