import React from 'react'
import {shallow} from 'enzyme'
import LocationImage from './'

describe('LocationImage', () => {
    describe('default component', () => {
        const wrapper = shallow(
            <LocationImage src="" size="1em" />
        )

        it('should have a data-abc-id prop', () => {
            expect(wrapper.prop('data-abc-id')).toBe('locationImage')
        })

        it('should assign the src attribute of the img to the default', () => {
            expect(wrapper.prop('src')).toBe('https://s3-us-west-2.amazonaws.com/abcfinancialsd-dev-static/NaoylBj.png')
        })

        it('should use the defaultLogo for placeholder', () => {
            expect(wrapper.prop('placeholder')).toBe('https://s3-us-west-2.amazonaws.com/abcfinancialsd-dev-static/NaoylBj.png')
        })
    })

    describe('will handle props', () => {
        const wrapper = shallow(
            <LocationImage className="fake-className" src="fake-img-src" size="111px" />
        )

        it('should assign the src attribute of the img', () => {
            expect(wrapper.prop('src')).toBe('fake-img-src')
        })

        it('should have the passed className', () => {
            expect(wrapper.hasClass('fake-className')).toBe(true)
        })
    })
})
