import React, {Component} from 'react'
import PropTypes from 'prop-types'
import BalancedImage from '../balancedImage'

const DEFAULT_IMAGE = 'https://s3-us-west-2.amazonaws.com/abcfinancialsd-dev-static/NaoylBj.png'

export default class LocationImage extends Component {
    render() {
        const {src, ...remain} = this.props
        return (
            <BalancedImage 
                placeholder={DEFAULT_IMAGE} 
                src={src === '' ? DEFAULT_IMAGE : src}
                data-abc-id="locationImage"
                {...remain}
            />
        )
    }
}

LocationImage.propTypes = {
    src: PropTypes.string,
    size: PropTypes.string.isRequired,
}

LocationImage.defaultProps = {
    src: DEFAULT_IMAGE,
}